import {Todo} from "./components/Todo";
import {AddTodo} from "./components/AddTodo";
import styled from "styled-components";
import {observer} from "mobx-react";
import {TodoList} from "./components/TodoList";
import {useTodoStore} from "./context/stores-context";
import {FILTER_TYPES} from "./stores/todo.store";
import {useState} from "react";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background: white;
  border: none;
  padding: 8px 4px;
  min-width: 320px;
  text-align: left;
  line-height: 24px;
  font-size: 13px;
  transition: all 0.2s ease;
  outline: none;
`

export const App = observer(() => {
    const todoStore = useTodoStore()

    const [todos, setTodos] = useState<Todo[]>( todoStore.show(FILTER_TYPES.ALL));
    const handleFilter = (filter: FILTER_TYPES) => {
        setTodos(todoStore.show(filter))
    }

    return (
        <Container>
            <Container>
                Count: {todos.length}
                <div>
                    <button onClick={() => handleFilter(FILTER_TYPES.ALL)}>All</button>
                    <button onClick={() => handleFilter(FILTER_TYPES.DONE)}>Done</button>
                </div>
            </Container>
            <AddTodo/>

            <TodoList>
                {todos.map(({id,title,done}) => (
                    <Todo key={id} id={id} title={title} done={done}/>
                ))}
            </TodoList>
        </Container>
    )
})
