import {useState} from "react";
import styled from "styled-components";
import Checkbox from "./Checkbox";
import Button from "./Button";
import ActionButton from "./ActionButton";
import RemoveIcon from "./RemoveIcon";
import {useTodoStore} from "../context/stores-context";
import {ITodo} from "../stores/todo.store";

const Item = styled.li`
  margin-top: -1px;
  display: flex;
  align-items: center;
  justify-content: space-between;

`;


export default function Todo({id,title,done}: ITodo) {
    const [checked, setChecked] = useState(done);
    const todoStore = useTodoStore()

    const handleChecked = () => {
        setChecked(!checked);
        todoStore.toggleDone(id);
    }
    const handleRemove = () => todoStore.removeTodo(id)
    return (
        <Item>
            <Button checked={checked} onClick={handleChecked}>
                <Checkbox checked={checked}/>
                <span>{title}</span>
            </Button>
            <ActionButton onClick={handleRemove} action='remove' display='flex' alignItems='center' justifyItems='center'>
                <RemoveIcon/>
            </ActionButton>
        </Item>
    );
}
