import {Todo} from "./components/Todo";
import {AddTodo} from "./components/AddTodo";
import styled from "styled-components";
import {observer} from "mobx-react";
import {TodoList} from "./components/TodoList";
import {useTodoStore} from "./context/stores-context";
import {useEffect, useState} from "react";
import {Input} from "./components/Input";
import {ITodo} from "./stores/todo.store";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background: white;
  border: none;
  padding: 8px 4px;
  min-width: 320px;
  text-align: left;
  line-height: 24px;
  font-size: 13px;
  transition: all 0.2s ease;
  outline: none;
`
const FilterButton = styled.button`
  background: white;
  border: none;
  padding: 8px 4px;
  text-align: left;
  line-height: 24px;
  font-size: 13px;
  transition: all 0.2s ease;
  outline: none;
  cursor: pointer;
  color: ${(props: { active: string; }) => props.active ? 'red' : 'black'};
`

export const App = observer(() => {
    const todoStore = useTodoStore()
    const [filter, setFilter] = useState<string>(todoStore.filter)
    const [search, setSearch] = useState<string>('')
    const [todos, ] = useState<ITodo[]>(todoStore.todos)
    const [checked, setChecked] = useState<Array<string>>([])


    const handleCheck = (id: string) => {
        setChecked((prevState) => {
            if (prevState.includes(id)) {
                return prevState.filter(item => item !== id)
            }
            return [...prevState, id]
        })
    }

    const handleFilter = (filter: string) => {
        setFilter(filter)
        todoStore.setFilter(filter)
    }

    const handleSearch = (e: any) => {
        e.persist();
        setSearch(e.target.value);
    }
    console.log('checked', checked);
    return (
        <Container>
            <Container>
                <div>Count: {todoStore.todos.length}</div>
                <div> {todoStore.show('DONE').length > 0 ? `Done: ${todoStore.show('DONE')?.length}` : null}</div>
                <div>
                    <Input
                        placeholder='Search'
                        onChange={handleSearch}
                        value={search}
                        type='text'/>
                    <FilterButton
                        active={filter === 'ALL'}
                        onClick={() => handleFilter('ALL')}>
                        All
                    </FilterButton>
                    <FilterButton
                        active={filter === 'DONE'}
                        onClick={() => handleFilter('DONE')}>
                        Done
                    </FilterButton>
                </div>

            </Container>
            <AddTodo/>

            <TodoList>
                {todos.map(({id, title, done}, index) => (
                    <div
                        key={id}
                        style={{
                            display: 'flex',
                            flexDirection: 'row',
                        }}>
                        <input checked={checked[index] === id} onChange={() => handleCheck(id)} type='checkbox'/>
                        <Todo id={id} title={title} done={done}/>
                    </div>
                ))}
            </TodoList>
        </Container>
    )
})
