import {Todo} from "./components/Todo";
import {AddTodo} from "./components/AddTodo";
import styled from "styled-components";
import {observer} from "mobx-react";
import {TodoList} from "./components/TodoList";
import {useTodoStore} from "./context/stores-context";
import {FILTER_TYPES} from "./stores/todo.store";
import {useState} from "react";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background: white;
  border: none;
  padding: 8px 4px;
  min-width: 320px;
  text-align: left;
  line-height: 24px;
  font-size: 13px;
  transition: all 0.2s ease;
  outline: none;
`
const FilterButton = styled.button`
  background: white;
  border: none;
  padding: 8px 4px;
  text-align: left;
  line-height: 24px;
  font-size: 13px;
  transition: all 0.2s ease;
  outline: none;
  cursor: pointer;
  color: ${(props: { active: FILTER_TYPES; }) => props.active ? 'red' : 'black'};
`

export const App = observer(() => {
    const todoStore = useTodoStore()
    const [filter, setFilter] = useState<string>(localStorage.getItem('filter') || FILTER_TYPES.ALL)
    const [todos, setTodos] = useState(todoStore.show(filter as FILTER_TYPES))
    const handleFilter = (filter: FILTER_TYPES) => {
        setFilter(filter)
        setTodos(todoStore.show(filter))
    }

    return (
        <Container>
            <Container>
                Count: {todos.length}
                <div>
                    <FilterButton
                        active={filter === FILTER_TYPES.ALL}
                        onClick={() => handleFilter(FILTER_TYPES.ALL)}>
                        All
                    </FilterButton>
                    <FilterButton
                        active={filter === FILTER_TYPES.DONE}
                        onClick={() => handleFilter(FILTER_TYPES.DONE)}>
                        Done
                    </FilterButton>
                </div>
            </Container>
            <AddTodo/>

            <TodoList>
                {todos.map(({id, title, done}) => (
                    <Todo key={id} id={id} title={title} done={done}/>
                ))}
            </TodoList>
        </Container>
    )
})
