import { nanoid } from 'nanoid';
import {action, makeObservable, observable, toJS} from 'mobx';

export interface ITodo {
  id: string;
  title: string;
  done: boolean;
}

export interface ITodoStore {
  filter: string;
  todos: ITodo[];
  addTodo: (title: string) => void;
  removeTodo: (id: string) => void;
  toggleDone: (id: string) => void;
  show: (filter: string) => ITodo[];
  setFilter: (filter: string) => void;
  deleteTodos: (ids: string[]) => void;
  changeStatus: (ids: string[], status: string) => void;
}

class TodoStore implements ITodoStore {

  @observable
  filter: string = localStorage.getItem('filter') ?? 'ALL';
  @observable
  todos: ITodo[] = localStorage.getItem('todos')
    ? JSON.parse(localStorage.getItem('todos')!)
    : [
        {
          id: nanoid(),
          title: "'lala'",
          done: false,
        },
      ];
  constructor() {
    makeObservable(this);
  }
  @action
  addTodo(title: string) {
    this.todos.push({
      id: nanoid(),
      title,
      done: false,
    });
    this.show(this.filter);
    localStorage.setItem('todos', JSON.stringify(this.todos));
  }
  @action
  removeTodo(id: string) {
    this.todos = this.todos.filter((todo) => todo.id !== id);
    this.show(this.filter);
    localStorage.setItem('todos', JSON.stringify(this.todos));
  }
  @action
  toggleDone(id: string) {
    this.todos.map((todo) => {
      if (todo.id === id) {
        todo.done = !todo.done;
      }
    });
    localStorage.setItem('todos', JSON.stringify(this.todos));
  }
  @action
  show(filter: string) {
    switch (filter) {
      case 'ALL':
        return toJS(this.todos);
      case 'DONE':
        return toJS(this.todos.filter((todo) => todo.done));
      default:
        return toJS(this.todos);
    }
  }
  @action
  setFilter(filter: string) {
    this.filter = filter;
    localStorage.setItem('filter', filter);
  }
  @action
  deleteTodos(ids: string[]) {
    const updateTodos = this.todos.filter((todo) => !ids.includes(todo.id));
    this.todos = updateTodos;
    localStorage.setItem('todos', JSON.stringify(updateTodos));
  }
  @action
  changeStatus(ids: string[], status: string): void {
    this.todos.map((todo) => {
      if (ids.includes(todo.id)) {
        todo.done = status === 'DONE';
      }
      return todo;
    });
    localStorage.setItem('todos', JSON.stringify(this.todos));
  }
}

const todoStore = new TodoStore();
export default todoStore;
