import {Todo} from "./components/Todo";
import {AddTodo} from "./components/AddTodo";
import styled from "styled-components";
import {observer} from "mobx-react";
import {TodoList} from "./components/TodoList";
import {useTodoStore} from "./context/stores-context";
import {FILTER_TYPES} from "./stores/todo.store";
import {useEffect, useState} from "react";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background: white;
  border: none;
  padding: 8px 4px;
  min-width: 320px;
  text-align: left;
  line-height: 24px;
  font-size: 13px;
  transition: all 0.2s ease;
  outline: none;
`
const FilterButton = styled.button`
  background: white;
  border: none;
  padding: 8px 4px;
  text-align: left;
  line-height: 24px;
  font-size: 13px;
  transition: all 0.2s ease;
  outline: none;
  cursor: pointer;
  color: ${(props: { active: FILTER_TYPES; }) => props.active ? 'red' : 'black'};
`

export const App = observer(() => {
    const todoStore = useTodoStore()
    const [filter, setFilter] = useState<FILTER_TYPES>(todoStore.filter)
    const [search, setSearch] = useState<string>('')
    const [todos, setTodos] = useState<Todo[]>(todoStore.todos)

    useEffect(() => {
        if (search.length > 2) {
            setTodos(prevState => {
                return prevState.find(todo => todo.title.includes(search)) ? prevState.filter(todo => todo.title.includes(search)) : prevState
            })
        }
    },[filter, search])

    const handleFilter = (filter: FILTER_TYPES) => {
        setFilter(filter)
    }
//as a user, I can search todo items by title
// as a user, I can select todo items and do a batch delete/done/undone actions
    const handleSearch = (e: any) => {
        e.persist();
        setSearch(e.target.value);
    }
    return (
        <Container>
            <Container>
                <div>Count: {todoStore.todos.length}</div>
                <div> {todoStore.show(FILTER_TYPES.DONE).length > 0 ? `Done: ${todoStore.show(FILTER_TYPES.DONE)?.length}` : null}</div>
                <div>
                    <FilterButton
                        active={filter === FILTER_TYPES.ALL}
                        onClick={() => handleFilter(FILTER_TYPES.ALL)}>
                        All
                    </FilterButton>
                    <FilterButton
                        active={filter === FILTER_TYPES.DONE}
                        onClick={() => handleFilter(FILTER_TYPES.DONE)}>
                        Done
                    </FilterButton>
                </div>
                <div>
                    <input
                        onChange={handleSearch}
                        value={search}
                        type='text'/>
                </div>

            </Container>
            <AddTodo/>

            <TodoList>
                {todoStore.show(filter).map(({id, title, done}) => (
                    <Todo key={id} id={id} title={title} done={done}/>
                ))}
            </TodoList>
        </Container>
    )
})
