import { createContext, ReactNode } from 'react';
import { useLocalStore } from "mobx-react-lite";
import { nanoid } from 'nanoid';
import { toJS } from 'mobx';

export const todoStore = createContext({} as ITodoStore);

export interface ITodo {
  id: string;
  title: string;
  done: boolean;
}

export interface ITodoStore {
  filter: string;
  todos: ITodo[];
  addTodo: (title: string) => void;
  removeTodo: (id: string) => void;
  toggleDone: (id: string) => void;
  show: (filter: string) => ITodo[];
  setFilter: (filter: string) => void;
  deleteTodos: (ids: string[]) => void;
  changeStatus: (ids: string[], status: string) => void;
}

interface ITodoProvider {
  children: ReactNode;
}

export const TodoProvider = ({ children }: ITodoProvider) => {
  const store = useLocalStore(
    () =>
      ({
        filter: localStorage.getItem('filter') ?? 'ALL',
        todos: localStorage.getItem('todos')
          ? JSON.parse(localStorage.getItem('todos')!)
          : ([
              {
                id: nanoid(),
                title: "'lala'",
                done: false,
              },
            ] as ITodo[]),
        addTodo(title: string) {
          this.todos.push({
            id: nanoid(),
            title,
            done: false,
          });
          localStorage.setItem('todos', JSON.stringify(this.todos));
        },
        removeTodo(id: string) {
          this.todos = this.todos.filter((todo) => todo.id !== id);
          localStorage.setItem('todos', JSON.stringify(this.todos));
        },
        toggleDone(id: string) {
          this.todos.map((todo) => {
            if (todo.id === id) {
              todo.done = !todo.done;
            }
          });
          localStorage.setItem('todos', JSON.stringify(this.todos));
        },
        show(filter: string) {
          switch (filter) {
            case 'ALL':
              return toJS(this.todos);
            case 'DONE':
              return toJS(this.todos.filter((todo) => todo.done));
            default:
              return toJS(this.todos);
          }
        },
        setFilter(filter: string) {
          this.filter = filter;
          localStorage.setItem('filter', filter);
        },
        deleteTodos(ids: string[]) {
          const updateTodos = this.todos.filter(
            (todo) => !ids.includes(todo.id),
          );
          this.todos = updateTodos;
          localStorage.setItem('todos', JSON.stringify(updateTodos));
        },
        changeStatus(ids: string[], status: 'DONE' | 'ACTIVE') {
          this.todos.map((todo) => {
            if (ids.includes(todo.id)) {
              todo.done = status === 'DONE';
            }
            return todo;
          });
          localStorage.setItem('todos', JSON.stringify(this.todos));
        },
      } as ITodoStore),
  );

  return <todoStore.Provider value={store}>{children}</todoStore.Provider>;
};
