import {nanoid} from 'nanoid'

export interface ITodo {
    id: string;
    title: string;
    done: boolean
}

export enum FILTER_TYPES {
    ALL = 'ALL',
    DONE = 'DONE',
}

export interface ITodoStore {
    filter: string;
    todos: ITodo[];
    addTodo: (title: string) => void;
    removeTodo: (id: string) => void;
    toggleDone: (id: string) => void;
    show: (filter: string) => ITodo[];
    setFilter: (filter: string) => void;
}

export const createTodoStore = () => {
    console.log('dasdsa',localStorage.getItem('filter') )
    return {
        filter: localStorage.getItem('filter') ?? 'ALL',
        todos: localStorage.getItem('todos') ? JSON.parse(localStorage.getItem('todos')!) : [{
            id: nanoid(),
            title: "'lala'",
            done: false
        },
        ] as ITodo[],
        addTodo(title: string) {
            this.todos.push({
                id: nanoid(),
                title,
                done: false
            });
            this.show(this.filter);
            localStorage.setItem('todos', JSON.stringify(this.todos))
        },
        removeTodo(id: string) {
            this.todos = this.todos.filter(todo => todo.id !== id);
            this.show(this.filter);
            localStorage.setItem('todos', JSON.stringify(this.todos))
        },
        toggleDone(id: string) {
            this.todos.map((todo) => {
                if (todo.id === id) {
                    todo.done = !todo.done;
                }
            });
            localStorage.setItem('todos', JSON.stringify(this.todos))
        },
        show(filter: string) {
            localStorage.setItem('filter', filter);
            switch (filter) {
                case FILTER_TYPES.ALL:
                    return this.todos;
                case FILTER_TYPES.DONE:
                    return this.todos.filter(todo => todo.done);
            }
        },
        setFilter(filter: FILTER_TYPES) {
            this.filter = filter === 'DONE' ? FILTER_TYPES.DONE : FILTER_TYPES.ALL;
            localStorage.setItem('filter', filter);
        }
    } as ITodoStore;
}
