import ActionButton from "./ActionButton";
import styled from "styled-components";
import {useState} from "react";

const Input = styled.input`
  background: white;
  border: none;
  padding: 8px 4px;
  min-width: 300px;
  text-align: left;
  line-height: 24px;
  font-size: 13px;
  transition: all 0.2s ease;
  outline: none;
`

const Container = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  background: white;
  border: none;
  border-top: 1px solid #ccc;
  border-bottom: 1px solid #ccc;
  padding: 8px 4px;
  margin: 10px auto;
  text-align: left;
  line-height: 24px;
  font-size: 13px;
  transition: all 0.2s ease;
  outline: none;
`


export function AddTodo() {
    const [value, setValue] = useState('');

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setValue(e.target.value);
    }
    const handleAdd = () => {
        console.log(value);
    }
    return (
        <Container>
            <Input
                onChange={handleChange}
                type='text'/>
            <ActionButton onClick={handleAdd} action='add'>
                Add
            </ActionButton>
        </Container>
    )
}
