import ActionButton from "./ActionButton";
import styled from "styled-components";

const Input = styled.input`
  background: white;
  border: none;
  border-top: 1px solid #ccc;
  border-bottom: 1px solid #ccc;
  padding: 8px 4px;
  min-width: 300px;
  text-align: left;
  line-height: 24px;
  font-size: 13px;
  transition: all 0.2s ease;
  outline: none;
`


const  Container = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    background: white;
    border: none;
    border-top: 1px solid #ccc;
    border-bottom: 1px solid #ccc;
    padding: 8px 4px;
    min-width: 320px;
    text-align: left;
    line-height: 24px;
    font-size: 13px;
    transition: all 0.2s ease;
    outline: none;
`


export function AddTodo() {
    return (
        <Container>
            <Input type='text'/>
            <ActionButton action='add'>
                Add
            </ActionButton>
        </Container>
    )
}
