import styled from "styled-components";

const ActionButton =  styled.button`
  background:  ${(props: { action: boolean }) => (props.action === 'add' ? "rgba(255,236,54,0.79)" : "rgba(222,40,40,0.85)")};;
  border: none;
  border-top: 1px solid #ccc;
  border-bottom: 1px solid #ccc;
  padding: 8px 4px;
  text-align: left;
  line-height: 24px;
  font-size: 13px;
  transition: all 0.2s ease;
  cursor: pointer;
  outline: none;

  &:hover,
  &:focus {
    background: #f5f5f5;
  }
`;

export function AddTodo() {
    return (
        <div>
            <input type='text'/>
            <ActionButton>
                Add
            </ActionButton>
        </div>
    )
}
